mapper.update:
update todos
set content =#{content}
where id = #{id} and user_id=#{user_id}

update todos
set heart =#{heart}
where id=#{id}

mapper.select:
select heart
from todos
where id=#{id}

select uid, tid
from hearts
where uid=#{uid} and tid=#{tid}

mapper.update:
update todos
set heart =heart+#{heart}
where id=#{id}
